package id.xms.xtrakernelmanager.ui.components

import androidx.compose.foundation.layout.*
import androidx.compose.material3.*
import androidx.compose.runtime.Composable
import androidx.compose.ui.Modifier
import androidx.compose.ui.unit.dp
import id.xms.xtrakernelmanager.ui.viewmodel.BatteryInfoViewModel

@Composable
fun SystemInfoCard(
    systemInfo: String,
    batteryInfo: BatteryInfoViewModel.BatteryInfo,
    modifier: Modifier = Modifier
) {
    Card(
        modifier = modifier.fillMaxWidth()
    ) {
        Column(
            modifier = Modifier.padding(16.dp)
        ) {
            Text(
                text = "System Information",
                style = MaterialTheme.typography.titleMedium
            )
            Spacer(modifier = Modifier.height(8.dp))

            // Battery Info Section
            Column {
                Text(
                    text = "Battery: ${batteryInfo.level}% " +
                          if (batteryInfo.isCharging) "(Charging: ${String.format("%.1f", batteryInfo.chargingWattage)}W)" else "",
                    style = MaterialTheme.typography.bodyMedium
                )
                Text(
                    text = "Temperature: ${String.format("%.1f", batteryInfo.temperature)}Â°C",
                    style = MaterialTheme.typography.bodyMedium
                )
                Text(
                    text = "Voltage: ${String.format("%.1f", batteryInfo.voltage)}V",
                    style = MaterialTheme.typography.bodyMedium
                )
            }

            Spacer(modifier = Modifier.height(8.dp))
            Divider()
            Spacer(modifier = Modifier.height(8.dp))

            // System Info Section
            Text(
                text = systemInfo,
                style = MaterialTheme.typography.bodyMedium
            )
        }
    }
}
